/* Fonte: https://developer.mozilla.org/en-US/docs/Web/CSS/Attribute_selectors */
/* <a> elements with a title attribute */
a[title] {
  color: purple;
}

/* <a> elements with an href matching "https://example.org" */
a[href="https://example.org"] {
  color: green;
}

/* <a> elements with an href containing "example" */
a[href*="example"] {
  font-size: 2em;
}

/* <a> elements with an href ending ".org" */
a[href$=".org"] {
  font-style: italic;
}

/* Fonte: https://www.w3.org/TR/selectors/#attribute-selectors */
span[class="example"] {
  font-size: 2em;
}

span[hello="Cleveland"][goodbye="Columbus"] {
  font-style: italic;
}

a[rel~="copyright"] {
  color: green;
}

a[hreflang=fr] {
  color: purple;
}

a[hreflang|="en"] {
  font-size: 2em;
}
